        // Device functions
        template <typename T> __device__ inline T      get_value        (float val);
        template <>           __device__ inline float  get_value<float> (float val) {return val;}
        template <>           __device__ inline float3 get_value<float3>(float val) {return make_float3(val, val, val);}

        __device__ inline float  my_sqrtf(float  val) {return sqrtf(val);}
        __device__ inline float3 my_sqrtf(float3 val) {return make_float3(sqrtf(val.x), sqrtf(val.y), sqrtf(val.z));}

        __device__ inline bool is_center(const unsigned short xC, const unsigned short yC, const short xI, const short yI) {
            return xI == xC && yI == yC;
        }
        __device__ inline bool is_not_discriminated(const float  meanC, const float  discriminatorC, const float  meanI, const float  discriminatorI) {
            return discriminatorC + discriminatorI <= 2.f * meanC   * meanI;
        }
        __device__ inline bool is_not_discriminated(const float3 meanC, const float3 discriminatorC, const float3 meanI, const float3 discriminatorI) {
            return discriminatorC.x + discriminatorI.x <= 2.f * meanC.x * meanI.x &&
                   discriminatorC.y + discriminatorI.y <= 2.f * meanC.y * meanI.y &&
                   discriminatorC.z + discriminatorI.z <= 2.f * meanC.z * meanI.z;
        }

        __device__ inline float dr2(
            const PtrStepSz<float> *&bufs,
            const PtrStepSz<unsigned char> &channelCounts,
            const PtrStepSz<float> &factors,
            const unsigned char nBufs,
            const unsigned short yC,
            const unsigned short xC,
            const unsigned short y,
            const unsigned short x
        ) {
            float d2 = 0.f;
            for (unsigned char i = 0; i < nBufs; i++)
                if (channelCounts[i] == 3) {
                    // Tested caching here, to no avail.
                    float d2Tmp = 0.f;
                    d2Tmp += (bufs[i](yC, xC*3  ) - bufs[i](y, x*3  )) * (bufs[i](yC, xC*3  ) - bufs[i](y, x*3  ));
                    d2Tmp += (bufs[i](yC, xC*3+1) - bufs[i](y, x*3+1)) * (bufs[i](yC, xC*3+1) - bufs[i](y, x*3+1));
                    d2Tmp += (bufs[i](yC, xC*3+2) - bufs[i](y, x*3+2)) * (bufs[i](yC, xC*3+2) - bufs[i](y, x*3+2));
                    d2 += d2Tmp * factors[i];
                } else if (channelCounts[i] == 1)
                    d2 += (bufs[i](yC, xC) - bufs[i](y, x)) * (bufs[i](yC, xC) - bufs[i](y, x)) * factors[i];
            return d2;
        }

        __device__ inline float johnson_mean_corr(const float nF, const float s2, const float m3) {
            return s2 > FLT_EPSILON ? (m3 / nF) / (6.f * s2 * nF) : 0.f;
        }
        __device__ inline float3 johnson_mean_corr(const float nF, const float3 s2, const float3 m3) {
            return make_float3(
                s2.x > FLT_EPSILON ? (m3.x / nF) / (6.f * s2.x * nF) : 0.f,
                s2.y > FLT_EPSILON ? (m3.y / nF) / (6.f * s2.y * nF) : 0.f,
                s2.z > FLT_EPSILON ? (m3.z / nF) / (6.f * s2.z * nF) : 0.f
            );
        }

        __device__ inline bool moon_mean_test(const int nC, const float meanC, const float m2C, const float meanI) {
            float t = t_quantiles[T_QUANTILE_MAX_INDEX];
            if (nC - 2 < T_QUANTILE_LUT_SIZE)
                t = t_quantiles[nC - 2];

            const float nCF = __int2float_rn(nC);
            const float se = t * sqrtf(m2C / (nCF * (nCF - 1.f)));
            return meanI >= meanC - se && meanI <= meanC + se;
        }
        __device__ inline bool moon_mean_test(const int nC, const float3 meanC, const float3 m2C, const float3 meanI) {
            float t = t_quantiles[T_QUANTILE_MAX_INDEX];
            if (nC - 2 < T_QUANTILE_LUT_SIZE)
                t = t_quantiles[nC - 2];

            const float nCF = __int2float_rn(nC);
            const float3 se = t * my_sqrtf(m2C / (nCF * (nCF - 1.f)));
            return meanI.x >= meanC.x - se.x && meanI.x <= meanC.x + se.x &&
                   meanI.y >= meanC.y - se.y && meanI.y <= meanC.y + se.y &&
                   meanI.z >= meanC.z - se.z && meanI.z <= meanC.z + se.z;
        }


        // CUDA Kernels
        template <typename T>
        __global__ void calculate_mean_vars_kernel(
            const unsigned short width,
            const unsigned short height,
            const PtrStepSz<int> *nPtrs,
            const PtrStepSz<T> *m2Ptrs,
            PtrStepSz<T> *meanVarPtrs
        ) {
            SET_ZYX(z, y, x)
            const float n = __int2float_rn(nPtrs[z](y, x));
            meanVarPtrs[z](y, x) = m2Ptrs[z](y, x) / (n * (n-1));
        }


        template <typename T>
        __global__ void johnson_mean_corrs_kernel(
            const unsigned short width,
            const unsigned short height,
            const PtrStepSz<int> *nPtrs,
            const PtrStepSz<T> *meanPtrs,
            const PtrStepSz<T> *m2Ptrs,
            const PtrStepSz<T> *m3Ptrs,
            PtrStepSz<T> *meanCorrPtrs
        ) {
            SET_ZYX(z, y, x)

            const int n = nPtrs[z](y, x);
            const float nF = __int2float_rn(n);
            const T &mean = meanPtrs[z](y, x);
            const T &m2 = m2Ptrs[z](y, x);
            const T &m3 = m3Ptrs[z](y, x);
            const T s2 = (m2 / (nF - 1.f));

            meanCorrPtrs[z](y, x) = mean + johnson_mean_corr(nF, s2, m3);
        }

        template <typename T>
        __global__ void mean_discriminators_kernel(
            const unsigned short width,
            const unsigned short height,
            const PtrStepSz<int> *nPtrs,
            const PtrStepSz<T> *meanPtrs,
            const PtrStepSz<T> *m2Ptrs,
            PtrStepSz<T> *meanDiscriminatorPtrs
        ) {
            SET_ZYX(z, y, x)

            const int n = nPtrs[z](y, x);
            const float nF = __int2float_rn(n);
            const T &mean = meanPtrs[z](y, x);
            const T &m2 = m2Ptrs[z](y, x);
            
            float t = t_quantiles[T_QUANTILE_MAX_INDEX];
            if (2 * n - 3 < T_QUANTILE_LUT_SIZE)
                t = t_quantiles[2 * n - 3];

            // HSTODO: Epsilon helps for caustic scene, where diffuse surface has zero variance
            meanDiscriminatorPtrs[z](y, x) = mean * mean - t * t * (m2/* + 0.01f*/) / (nF * (nF - 1.f));
        }

        template <typename T>
        __global__ void filter_kernel(
            const unsigned short width,
            const unsigned short height,
            const bool denoiseFilm,
            const float dSFactor,
            const unsigned char rad,
            const unsigned short rad2,
            const BrdReplicate<int> bI1,
            const BrdReplicate<T> bFT,
            const BrdReplicate<float3> bF3,
            const PtrStepSz<int> *nPtrs,
            const PtrStepSz<T> *meanPtrs,
            const PtrStepSz<T> *filmPtrs,
            const PtrStepSz<T> *m2Ptrs,
            const PtrStepSz<float3> film,
            const PtrStepSz<float> *gBufPtrs,
            const PtrStepSz<unsigned char> gBufChannelCounts,
            const PtrStepSz<float> gBufDRFactors,
            const unsigned char nGBufs,
            const PtrStepSz<T> *meanCorrPtrs,
            const PtrStepSz<T> *discriminatorPtrs,
            PtrStepSz<T> *filmFilteredPtrs,
            PtrStepSz<float3> filmFiltered
        ) {
            SET_ZYX(z, yC, xC)
            SET_OUTER(yMin, yMax, xMin, xMax)

#if MEMFNC == 1
            const T meanC = meanPtrs[z](yC, xC);
#elif MEMFNC == 0
            const T meanCorrC = meanCorrPtrs[z](yC, xC);
            const T discriminatorC = discriminatorPtrs[z](yC, xC);
#endif

            T num = get_value<T>(0.f);
            T filmNum = get_value<T>(0.f);
            float3 filmNum3 = make_float3(0.0f, 0.0f, 0.0f);
            float den = 0.f;
            for (short yI = yMin; yI < yMax; yI++)
                for (short xI = xMin; xI < xMax; xI++) {
                    SET_INNER(y, x, dS2)
                    if (is_center(xC, yC, xI, yI)) {
                        if (denoiseFilm && z == 0)
                            filmNum3 = filmNum3 + film(yI, xI);
                        filmNum = filmNum + filmPtrs[z](yI, xI);
                        den += 1.f;
#if MEMFNC == 1
                    } else if (moon_mean_test(nPtrs[z](yC, xC), meanPtrs[z](yC, xC), m2Ptrs[z](yC, xC), bFT.at(yI, xI, meanPtrs[z].data, meanPtrs[z].step)))
#elif MEMFNC == 0
                    } else if (is_not_discriminated(meanCorrC, discriminatorC, bFT.at(yI, xI, meanCorrPtrs[z].data, meanCorrPtrs[z].step), bFT.at(yI, xI, discriminatorPtrs[z].data, discriminatorPtrs[z].step)))
#endif
                    {
                        float w = expf(dS2 * dSFactor + dr2(gBufPtrs, gBufChannelCounts, gBufDRFactors, nGBufs, yC, xC, y, x));// * __int2float_rn(bI1.at(yI, xI, nPtrs[z].data, nPtrs[z].step)); // No adaptive sampling

                        if (denoiseFilm && z == 0)
                            filmNum3 = filmNum3 + w * bF3.at(yI, xI, film.data, film.step);
                        filmNum = filmNum + w * bFT.at(yI, xI, filmPtrs[z].data, filmPtrs[z].step);

                        den += w;
                    }
                }

            if (denoiseFilm && z == 0)
                filmFiltered(yC, xC) = filmNum3 / den;
            filmFilteredPtrs[z](yC, xC) = filmNum / den;
        }

