                    } else if (moon_mean_test(nPtrs[z](yC, xC), meanPtrs[z](yC, xC), m2Ptrs[z](yC, xC), bFT.at(yI, xI, meanPtrs[z].data, meanPtrs[z].step)))
#elif MEMFNC == 0
                    } else if (is_not_discriminated(meanCorrC, discriminatorC, bFT.at(yI, xI, meanCorrPtrs[z].data, meanCorrPtrs[z].step), bFT.at(yI, xI, discriminatorPtrs[z].data, discriminatorPtrs[z].step)))
#endif
                    {
                        float w = expf(dS2 * dSFactor + dr2(gBufPtrs, gBufChannelCounts, gBufDRFactors, nGBufs, yC, xC, y, x));// * __int2float_rn(bI1.at(yI, xI, nPtrs[z].data, nPtrs[z].step)); // No adaptive sampling

                        if (denoiseFilm && z == 0)
                            filmNum3 = filmNum3 + w * bF3.at(yI, xI, film.data, film.step);
                        else
                            filmNum = filmNum + w * bFT.at(yI, xI, filmPtrs[z].data, filmPtrs[z].step);

                        den += w;
                    }
                }

            if (denoiseFilm && z == 0)
                filmFiltered(yC, xC) = filmNum3 / den;
            else
                filmFilteredPtrs[z](yC, xC) = filmNum / den;
        }

#undef SET_ZYX
#undef SET_OUTER
#undef SET_INNER


        void setup() {
            cudaSafeCall(cudaDeviceSetLimit(cudaLimitMallocHeapSize, 1024 * 1024 * 1024));
            cudaSafeCall(cudaDeviceSetCacheConfig(cudaFuncCachePreferL1)); // We use no shared memory -> prefer L1 cache over shared memory
        }

        void synchronize(cudaStream_t stream) {
            cudaSafeCall(cudaStreamSynchronize(stream));
        }

        template <typename T>
        void calculate_mean_vars(
            const unsigned short ptrCount,
            const unsigned short width,
            const unsigned short height,
            const PtrStepSzb &nPtrs,
            const PtrStepSzb &m2Ptrs,
            PtrStepSzb meanVarPtrs,
            cudaStream_t stream
        ) {
            const dim3 block(32, 8);
            const dim3 grid(divUp(width, block.x), divUp(height, block.y), ptrCount);

            auto nPtr = (PtrStepSz<int> *) nPtrs.ptr();
            auto m2Ptr = (PtrStepSz<T> *) m2Ptrs.ptr();
            auto meanVarPtr = (PtrStepSz<T> *) meanVarPtrs.ptr();

            // Standard deviation
            calculate_mean_vars_kernel<T><<<grid, block, 0, stream>>>(
                width,
                height,
                nPtr,
                m2Ptr,
                meanVarPtr // out
            );
        }
