        void calculate_mean_vars<float3>(
            const unsigned short, const unsigned short, const unsigned short, const PtrStepSzb &, const PtrStepSzb &, PtrStepSzb, cudaStream_t
        );


        template <typename T>
        void filter(
            const unsigned short ptrCount,
            const unsigned short width,
            const unsigned short height,
            const float dSFactor,
            const unsigned char radius,
            const bool denoiseFilm,
            const PtrStepSzb &nPtrs,
            const PtrStepSzb &meanPtrs,
            const PtrStepSzb &m2Ptrs,
            const PtrStepSzb &m3Ptrs,
            const PtrStepSzb &filmPtrs,
            const PtrStepSzb &film,
            const PtrStepSzb &gBufPtrs,
            const PtrStepSzb &gBufChannelCounts,
            const PtrStepSzb &gBufDRFactors,
            const unsigned char nGBufs,
            PtrStepSzb meanCorrPtrs,
            PtrStepSzb discriminatorPtrs,
            PtrStepSzb filmFilteredPtrs,
            PtrStepSzb filmFiltered,
            cudaStream_t stream
        ) {
            const dim3 block(32, 8);
            const dim3 grid(divUp(width, block.x), divUp(height, block.y), ptrCount);

            const BrdReplicate<int>    bI1(height, width);
            const BrdReplicate<float3> bF3(height, width);
            const BrdReplicate<T>      bFT(height, width);

            auto nPtr    = (PtrStepSz<int> *)nPtrs.ptr();
            auto meanPtr = (PtrStepSz<T>   *)meanPtrs.ptr();
            auto m2Ptr   = (PtrStepSz<T>   *)m2Ptrs.ptr();
            auto m3Ptr   = (PtrStepSz<T>   *)m3Ptrs.ptr();
            auto filmPtr = (PtrStepSz<T>   *)filmPtrs.ptr();

            auto gBufsPtr = (PtrStepSz<float> *)gBufPtrs.ptr();
            auto gBufDRFactorsPtr = (PtrStepSz<float>)gBufDRFactors;

            auto meanCorrPtr      = (PtrStepSz<T> *)meanCorrPtrs.ptr();
            auto discriminatorPtr = (PtrStepSz<T> *)discriminatorPtrs.ptr();
            auto filmFilteredPtr  = (PtrStepSz<T> *)filmFilteredPtrs.ptr();

            const unsigned short radius2 = radius * radius; // Precompute (instead of calculating it over and over in the kernels ~-1 ms)

#if MEMFNC == 0
            johnson_mean_corrs_kernel<T><<<grid, block, 0, stream>>>(
                width,
                height,
                nPtr, meanPtr,
                m2Ptr,
                m3Ptr,
                meanCorrPtr
            );
            mean_discriminators_kernel<T><<<grid, block, 0, stream>>>(
                width,
                height,
                nPtr, meanCorrPtr,
                m2Ptr,
                discriminatorPtr // out
            );
#endif
            filter_kernel<T><<<grid, block, 0, stream>>>(
                width,
                height,
                denoiseFilm,
                dSFactor, radius, radius2,
                bI1, bFT, bF3,
                nPtr, meanPtr,
                filmPtr,
                m2Ptr,
                (PtrStepSz<float3>)film,
                gBufsPtr, gBufChannelCounts, gBufDRFactorsPtr, nGBufs,
                meanCorrPtr, discriminatorPtr,
                filmFilteredPtr, // out
                (PtrStepSz<float3>)filmFiltered
            );
        }
        template
        void filter<float>(
            const unsigned short, const unsigned short, const unsigned short, const float, const unsigned char, const bool, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const unsigned char, PtrStepSzb, PtrStepSzb, PtrStepSzb, PtrStepSzb, cudaStream_t
        );
        template
        void filter<float3>(
            const unsigned short, const unsigned short, const unsigned short, const float, const unsigned char, const bool, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const PtrStepSzb &, const unsigned char, PtrStepSzb, PtrStepSzb, PtrStepSzb, PtrStepSzb, cudaStream_t
        );
